import React from 'react';
import './App.css';
import SearchResults from '../SearchResults/SearchResults.js';
import Playlist from '../Playlist/Playlist.js';
import SearchBar from '../SearchBar/SearchBar.js';
import Spotify from '../../util/Spotify.js';


class App extends React.Component {
  constructor(props) {
  super(props);
  this.updatePlaylistName=this.updatePlaylistName.bind(this);
  this.addTrack = this.addTrack.bind(this);
  this.removeTrack=this.removeTrack.bind(this);
  this.savePlaylist=this.savePlaylist.bind(this);
  this.search=this.search.bind(this);
  this.state = {
          searchResults: [], // array of things\
          playlistName:'',
          playlistTracks:[{name:'',
           artist:'',
           album:''
        }]

  }}

  search(searchTerm){
    Spotify.search(searchTerm).then(searchResults=>{
    this.setState({SearchResults:searchResults});
  });
  console.log(searchTerm);
}
savePlaylist() {
    const trackUris = this.state.playlistTracks.map(track => track.uri);
    Spotify.savePlaylist(this.state.playlistName, trackUris).then(() => {
      this.setState({
        playlistName: 'New Playlist',
        playlistTracks: []
      });
    });
  }
  updatePlaylistName(name){
    this.setState({playlistName:name});
  }
  removeTrack(track) {
    let tracks = this.state.playlistTracks;
    const removeTrack = tracks.filter(playlistTrack => track.id !== playlistTrack.id);
    this.setState({ playlistTracks: removeTrack });
}
  addTrack(track) {
    if (this.state.playlistTracks.every(plTrack => plTrack.id !== track.id)) {
      let newPlaylistTracks = this.state.playlistTracks.push(track);
      this.setState({
        playlistTracks: newPlaylistTracks
      });
    }
  }

  render(){
    return (
      <div>
  <h1>Ja<span className="highlight">mmm</span>ing</h1>
  <div className="App">
    <SearchBar onSearch={this.search}/>
    <div className="App-playlist">
      <SearchResults searchResults={this.state.searchResults}/>
      <Playlist onSave={this.savePlaylist} onNameChange={this.updatePlaylistName} onRemove={this.removeTrack}  playlistName={this.state.playlistName} playlistTracks={this.state.playlistTracks}/>
    </div>
  </div>
</div>
    );
  }
}

export default App;
